// LoginPage.js

import React, { useState } from 'react';
import styled from 'styled-components';

const LoginPage = ({ setIsAuthenticated, setActive }) => { // Recebe setIsAuthenticated como propriedade
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
  
    const handleLogin = () => {
      if (username === 'flaviota' && password === 'flaviota') {
        // Autenticação bem-sucedida, você pode definir a autenticação como verdadeira aqui.
        setIsAuthenticated(true); // Define como autenticado usando a propriedade passada
        setActive('login'); // Define 'login' como a página ativa
        alert('Login bem-sucedido!');
      } else {
        alert('Usuário ou senha incorretos');
      }
    };

  return (
    <LoginPageWrapper>
      <div className="main-login">
        <div className="left-login">
          <h1>Faça login<br />E entre para o nosso time</h1>
          <img src="investi.png" className="left-login-image" alt="Investiment animation" />
        </div>
        <div className="right-login">
          <div className="card-login">
            <h1>LOGIN</h1>
            <div className="textfield">
              <label htmlFor="usuario">Usuário</label>
              <input
                type="text"
                name="usuario"
                placeholder="Usuário"
                id="login"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>
            <div className="textfield">
              <label htmlFor="senha">Senha</label>
              <input
                type="password"
                name="senha"
                placeholder="Senha"
                id="senha"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <button className="btn-login" onClick={handleLogin}>Login</button>
          </div>
        </div>
      </div>
    </LoginPageWrapper>
  );
};

const LoginPageWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  background-color: #2f2841;
  height: 100vh; /* Define a altura da tela inteira */
  border-radius: 20px;
  box-shadow: 0px 10px 40px #00000056;
  color: #00ff88;

  h2 {
    margin-bottom: 10px;
  }

  input {
    width: 100%;
    margin: 10px 0;
    padding: 10px;
    border: none;
    border-radius: 5px;
  }

  button {
    background-color: #00ff88;
    color: #2b134b;
    border: none;
    border-radius: 8px;
    padding: 10px;
    cursor: pointer;
    font-weight: bold;
    width: 100%;
    margin-top: 10px;
  }

  button:hover {
    background-color: #00cc66;
  }
`;

export default LoginPage;
