// LoginPage.js

import React, { useState } from 'react';
import styled from 'styled-components';

const LoginPage = ({ setIsAuthenticated, setActive }) => { // Recebe setIsAuthenticated como propriedade
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
  
    const handleLogin = () => {
      if (username === 'flaviota' && password === 'flaviota') {
        // Autenticação bem-sucedida, você pode definir a autenticação como verdadeira aqui.
        setIsAuthenticated(true); // Define como autenticado usando a propriedade passada
        setActive('login'); // Define 'login' como a página ativa
        alert('Login bem-sucedido!');
      } else {
        alert('Usuário ou senha incorretos');
      }
    };

  return (
    <LoginPageWrapper>
      <h2>Login</h2>
      <input
        type="text"
        placeholder="Usuário"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Senha"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Entrar</button>
    </LoginPageWrapper>
  );
};

const LoginPageWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  background-color: #2f2841;
  height: 100vh; /* Define a altura da tela inteira */
  border-radius: 20px;
  box-shadow: 0px 10px 40px #00000056;
  color: #00ff88;

  h2 {
    margin-bottom: 10px;
  }

  input {
    width: 100%;
    margin: 10px 0;
    padding: 10px;
    border: none;
    border-radius: 5px;
  }

  button {
    background-color: #00ff88;
    color: #2b134b;
    border: none;
    border-radius: 8px;
    padding: 10px;
    cursor: pointer;
    font-weight: bold;
    width: 100%;
    margin-top: 10px;
  }

  button:hover {
    background-color: #00cc66;
  }
`;

export default LoginPage;
